import React, { useState } from "react";
import api from "../../services/api.js";
import DropDown from "../DropDown/DropDown.js";
import "./FormCategories.css";

const Categories = (props) => {
  const [isContry, setContry] = useState("");
  const [isCategorie, setCategorie] = useState("");
  const [isListContrys, setListContrys] = useState([]);
  const [isListCategories, setListCategories] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const [isError, setError] = useState("");

  const LISTCONTRYS = [
    { id: "US", name: "USA" },
    { id: "AU", name: "AUSTRIA" },
    { id: "BR", name: "BRASIL" },
    { id: "CA", name: "CANADA" },
    { id: "FR", name: "FRANCIA" },
    { id: "IN", name: "REINO UNIDO" },
  ];

  const LISTCATEGORIAS = [
    { id: "abc", name: "PRUEBA 1" },
    { id: "dfg", name: "PRUEBA 2" },
  ];
  const getProducts = (params) => {
    console.log(`isContry: ${params}` );
    setLoading(true);
    api.getProducts(params).then(
      (resp) => {
        setListCategories(resp.data);
        setLoading(false);
      },
      (err) => {
        setLoading(false);
        setError("ERROR. Favor de comunicarse con sistemas.");
      }
    );
  };

  const contryHandler = (event) => {
    console.log(event);
    setContry(prevState => { event });

    getProducts(isContry);
  };

  const categoriesHandler = (event) => {
    console.log(event);
    setCategorie((preStatus) => {
      return  event;
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(
      {
        isCategorie,
        isContry
      }
    );
  }

  return (
    <div className="contenedor">
      <div className="categorias">
        <form onSubmit={handleSubmit}>
          <DropDown
            onChangeFilter={contryHandler}
            label={"Seleciona País:"}
            items={LISTCONTRYS}
          ></DropDown>
          <DropDown
            onChangeFilter={categoriesHandler}
            label={"Seleciona Categoria:"}
            items={LISTCATEGORIAS}
          ></DropDown>
          <button type="submit" className="btn btn-info btn-block">
            Buscar
          </button>
        </form>
      </div>
    </div>
  );
};

export default Categories;
