import React, { useState, useEffect } from "react";
import Filter from "../Filter/Filter";
import "./Products.css";
import ReactHTMLTableToExcel from "react-html-table-to-excel";
import { useParams } from "react-router-dom";
import api from "../../services/api";
import { useHistory } from "react-router-dom";

const Products = (props) => {
  const PRODUCTS = [
    {
      contry: "1",
      JobTitle: "Chef Manager",
      EmailAddress: "Julian_Vallins9159@gembat.biz",
      FirstNameLastName: "Julian Vallins",
    },
    {
      contry: "2",
      JobTitle: "Cash Manager",
      EmailAddress: "Benjamin_Owen7178@famism.biz",
      FirstNameLastName: "Benjamin Owen",
    },
    {
      contry: "3",
      JobTitle: "Business Broker",
      EmailAddress: "Hadley_Shaw689@liret.org",
      FirstNameLastName: "Hadley Shaw",
    },
    {
      contry: "4",
      JobTitle: "Cash Manager",
      EmailAddress: "Agnes_Lewin9759@jiman.org",
      FirstNameLastName: "Agnes Lewin",
    },
  ];
  let history = useHistory();
  let { country, categoryID } = useParams();
  const [isFilter, setFiltered] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [isError, setError] = useState("");
  const [isListProducts, setListProducts] = useState([]);

  useEffect(() => {
    const validateParams =
      !validateEmptyData(country) && !validateEmptyData(categoryID);
    const parSession = !validateEmptyData(localStorage.getItem("parameters"));
    console.log(localStorage.getItem("parameters"));
    console.log(country, categoryID);
    if (!validateParams && !parSession) {
      history.push("/");
    } else {
      if (validateParams) {
        getProductsPar({ country, categoryID });
      } else {
        getProductsPar(JSON.parse(parSession));
      }
    }
  }, []);

  const getProductsPar = (any) => {
    /*setLoading(true);
    const params = {
      country: "US",
      keyword: "iphone",
      categoryID: "aps",
      page: "1",
      filter:
        "aHR0cHM6Ly93d3cuYW1hem9uLmNvbS9zP2s9aXBob25lJnJoPXBfbl9jb25kaXRpb24tdHlwZSUzQVVzZWQmZGMmcWlkPTE2MTI0MTg5NTMmcmVmPXNyX25yX3Bfbl9jb25kaXRpb24tdHlwZV8y",
    };
    api.getProducts(params).then(
      (resp) => {
        setLoading(false);
        setListProducts(resp.data.docs);
      },
      (err) => {
        setLoading(false);
        setError(err);
      }
    );*/
  };

  const validateEmptyData = (data) => {
    let validate = false;
    if (data === undefined || data === null || data === "") {
      validate = true;
    }
    return validate;
  };

  const filterChangeHandler = (filter) => {
    setFiltered(filter);
  };

  const filteredListProduct = isListProducts.filter((product) => {
    if (isFilter.trim().length > 0) {
      return (
        product.product_title.toLowerCase().indexOf(isFilter.toLowerCase()) > -1
      );
    } else {
      return isListProducts;
    }
  });

  return (
    <div className="m-5">
      <div className="buscador">
        <Filter onChangeFilter={filterChangeHandler}></Filter>
        <div>
          <ReactHTMLTableToExcel
            className="btn btn-info"
            table="producto"
            filename="Productos"
            sheet="pagina 1"
            buttonText="Exportar a Excel"
          ></ReactHTMLTableToExcel>
        </div>
      </div>
      {isListProducts && 
      <div className="contenedor-tabla">
        <table className="table table-bordered table-dark" id="producto">
          <thead >
            <tr>
              <th>ID</th>
              <th>Producto</th>
              <th>Evaluaci√≥n</th>
              <th>Vista Previa</th>
            </tr>
          </thead>
          <tbody>
            {filteredListProduct.map((prodcut, index) => {
              return [
                <tr key={index}>
                  <th>{prodcut.product_id}</th>
                  <th>{prodcut.product_title}</th>
                  <th>{prodcut.evaluate_rate}</th>
                  <th>
                    <img  className="img-previa img-circle" src={prodcut.product_main_image_url}></img>

                  </th>
                </tr>,
              ];
            })}
          </tbody>
        </table>
      </div>
      }

    </div>
  );
};

export default Products;
