import React, { useState, useEffect } from "react";
import Filter from "../Filter/Filter";
import "./Products.css";
import ReactHTMLTableToExcel from "react-html-table-to-excel";
import { useParams } from "react-router-dom";
import api from "../../services/api";

const Products = (props) => {
  const PRODUCTS = [
    {
      contry: "1",
      JobTitle: "Chef Manager",
      EmailAddress: "Julian_Vallins9159@gembat.biz",
      FirstNameLastName: "Julian Vallins",
    },
    {
      contry: "2",
      JobTitle: "Cash Manager",
      EmailAddress: "Benjamin_Owen7178@famism.biz",
      FirstNameLastName: "Benjamin Owen",
    },
    {
      contry: "3",
      JobTitle: "Business Broker",
      EmailAddress: "Hadley_Shaw689@liret.org",
      FirstNameLastName: "Hadley Shaw",
    },
    {
      contry: "4",
      JobTitle: "Cash Manager",
      EmailAddress: "Agnes_Lewin9759@jiman.org",
      FirstNameLastName: "Agnes Lewin",
    },
  ];

  let { contry, categorie } = useParams();

  const [isFilter, setFiltered] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [isError, setError] = useState("");
  const [isListProducts, setListProducts] = useState([]);
  const [isRedirect, setRedirect] = useState(false);

  useEffect(() => {
    const validateParams =
      !validateEmptyData(contry) && !validateEmptyData(categorie);

    setLoading(true);
    api.getProducts({ contry, categorie }).then(
      (resp) => {
        setLoading(false);
        setListProducts(PRODUCTS);
        setRedirect(true);
      },
      (err) => {
        setListProducts(PRODUCTS);
        setLoading(false);
        setError(err);
      }
    );
  }, []);

  const validateEmptyData = (data) => {
    let validate = false;
    if (data === undefined || data === null || data === "") {
      validate = true;
    }
    return validate;
  };

  const filterChangeHandler = (filter) => {
    setFiltered(filter);
  };

  const filteredListProduct = isListProducts.filter((product) => {
    if (isFilter.trim().length > 0) {
      return (
        product.JobTitle.toLowerCase().indexOf(isFilter.toLowerCase()) > -1
      );
    } else {
      return isListProducts;
    }
  });

  return (
    <div>
      <div className="buscador">
        <Filter onChangeFilter={filterChangeHandler}></Filter>
        <div>
          <ReactHTMLTableToExcel
            className="btn btn-success"
            table="producto"
            filename="Productos"
            sheet="pagina 1"
            buttonText="Exportar a Excel"
          ></ReactHTMLTableToExcel>
        </div>
      </div>

      <div className="contenedor-tabla">
        <table className="table" id="producto">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">First</th>
              <th scope="col">Last</th>
              <th scope="col">Handle</th>
            </tr>
          </thead>
          <tbody>
            {filteredListProduct.map((prodcut, index) => {
              return [
                <tr key={index}>
                  <th scope="col">{prodcut.contry}</th>
                  <th scope="col">{prodcut.JobTitle}</th>
                  <th scope="col">{prodcut.EmailAddress}</th>
                  <th scope="col">{prodcut.FirstNameLastName}</th>
                </tr>,
              ];
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Products;
