import React, { useState, useEffect } from "react";
import Categories from "./components/Categories/FormCategories.js";
import Products from "./components/Products/Products.js";
import api from "./services/api.js";
import { BrowserRouter, Route, Switch, useHistory } from 'react-router-dom';

function App() {
  let history = useHistory();

  const prodcuts = [
    {
      contry: "1",
      JobTitle: "Chef Manager",
      EmailAddress: "Julian_Vallins9159@gembat.biz",
      FirstNameLastName: "Julian Vallins",
    },
    {
      contry: "2",
      JobTitle: "Cash Manager",
      EmailAddress: "Benjamin_Owen7178@famism.biz",
      FirstNameLastName: "Benjamin Owen",
    },
    {
      contry: "3",
      JobTitle: "Business Broker",
      EmailAddress: "Hadley_Shaw689@liret.org",
      FirstNameLastName: "Hadley Shaw",
    },
    {
      contry: "4",
      JobTitle: "Cash Manager",
      EmailAddress: "Agnes_Lewin9759@jiman.org",
      FirstNameLastName: "Agnes Lewin",
    },
  ];

  const [isLoading, setLoading] = useState(false);
  const [isError, setError] = useState("");
  const [isListProducts, setListProducts] = useState([]);


  useEffect(() => {
    if (JSON.parse(localStorage.getItem('parameters'))) {
      handlerSearch(JSON.parse(localStorage.getItem('parameters')));
    }
  }, []);

  const handlerSearch = (event) => {
    localStorage.setItem('parameters', JSON.stringify(event));
    setLoading(true);
    api.getProducts(event).then(
      (resp) => {
        setLoading(false);
        setListProducts(prodcuts);
      },
      (err) => {
        setListProducts(prodcuts);
        setLoading(false);
        setError(err);
      }
    );
  };


  return (
    <div className="App container">
      <BrowserRouter>
        <div>
          <Switch>
            <Route
              path="/"
              render={() => <Categories onSearch={handlerSearch} />}  />
            <Route
              path="/productos"
              render={() => <Products items={isListProducts} />}  />
          </Switch>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
