import React, { useState } from "react";
import Categories from "./components/Categories/FormCategories.js";
import Products from "./components/Products/Products.js";
import api from "./services/api.js";

function App() {
  const prodcuts = [
    {
      contry: "1",
      JobTitle: "Chef Manager",
      EmailAddress: "Julian_Vallins9159@gembat.biz",
      FirstNameLastName: "Julian Vallins",
    },
    {
      contry: "2",
      JobTitle: "Cash Manager",
      EmailAddress: "Benjamin_Owen7178@famism.biz",
      FirstNameLastName: "Benjamin Owen",
    },
    {
      contry: "3",
      JobTitle: "Business Broker",
      EmailAddress: "Hadley_Shaw689@liret.org",
      FirstNameLastName: "Hadley Shaw",
    },
    {
      contry: "4",
      JobTitle: "Cash Manager",
      EmailAddress: "Agnes_Lewin9759@jiman.org",
      FirstNameLastName: "Agnes Lewin",
    },
  ];

  const [isLoading, setLoading] = useState(false);
  const [isError, setError] = useState("");
  const [isListProducts, setListProducts] = useState([]);
  const [value, setValue] = useStateWithLocalStorage(
    'myValueInLocalStorage'
  );

  const handlerSearch = (event) => {
    setValue(event.target.value);
    setLoading(true);
    api.getProducts(event).then(
      (resp) => {
        setLoading(false);
        setListProducts(prodcuts);
      },
      (err) => {
        setListProducts(prodcuts);
        setLoading(false);
        setError(err);
      }
    );
  };

  const useStateWithLocalStorage = localStorageKey => {
    const [value, setValue] = React.useState(
      localStorage.getItem(localStorageKey) || ''
    );

    React.useEffect(() => {
      localStorage.setItem(localStorageKey, value);
    }, [value]);
    console.log([value, setValue]);
    return [value, setValue];
  };

  return (
    <div className="App container">
      <Categories onSearch={handlerSearch}></Categories>
      <Products items={isListProducts}></Products>
    </div>
  );
}

export default App;
