import React, { useState } from "react";
import Filter from "../Filter/Filter";
import "./Products.css";
import ReactHTMLTableToExcel from "react-html-table-to-excel";

const Products = (props) => {
  const [isFilter, setFiltered] = useState("");
  const filterChangeHandler = (filter) => {
    setFiltered(filter);
  };

  console.log(props);

  const filteredListProduct = props.items.filter((product) => {
    if (isFilter.trim().length > 0) {
      return (
        product.JobTitle.toLowerCase().indexOf(isFilter.toLowerCase()) > -1
      );
    } else {
      return props.items;
    }
  });

  return (
    <div>
      <div className="buscador">
        <Filter onChangeFilter={filterChangeHandler}></Filter>
        <div>
          <ReactHTMLTableToExcel
            className="btn btn-info"
            table="producto"
            filename="Productos"
            sheet="pagina 1"
            buttonText="Exportar a Excel"
          ></ReactHTMLTableToExcel>
        </div>
      </div>

      <div className="contenedor-tabla">
        <table className="table" id="producto">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">First</th>
              <th scope="col">Last</th>
              <th scope="col">Handle</th>
            </tr>
          </thead>
          <tbody>
            {filteredListProduct.map((prodcut, index) => {
              return [
                <tr key={index}>
                  <th scope="col">{prodcut.contry}</th>
                  <th scope="col">{prodcut.JobTitle}</th>
                  <th scope="col">{prodcut.EmailAddress}</th>
                  <th scope="col">{prodcut.FirstNameLastName}</th>
                </tr>,
              ];
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Products;
