import axios from "axios";

const instance = axios.create({
  baseURL: "https://amazon24.p.rapidapi.com/api/",
  headers: {
    "x-rapidapi-key": "fd4e36dfedmsh4216d69bd981ae1p19b11ajsn28b1a26d2b09",
    "x-rapidapi-host": "amazon24.p.rapidapi.com",
  },
});

export default {
  getCategories: (params) =>
    instance({
      method: "GET",
      url: "/category",
      params: {
        country: params,
      },
    }),
  getProducts: (params) => {
    instance({
      method: "GET",
      url: "/product",
      params: {
        country: params,
      },
    });
  },
};
