import React, { useState } from "react";
import api from "../../services/api";
import Filter from "../Filter/Filter";
import "./Products.css";

const Products = (props) => {

  const [isLoading, setLoading] = useState(false);
  const [isError, setError] = useState("");
  const [isListProducts, setListProducts] = useState([]);
  const [isFilter, setFilter] = useState("");
  const [isFilteredListProduct, setFilteredListProduct] = useState(preProducts);
  const [isFiltered, setFiltered] = useState("");

  const getListProducts = () => {
    setLoading(true);
    api.getProducts(props.props).then(
      (resp) => {
        setListProducts(resp.data);
        setLoading(false);
      },
      (err) => {
        setLoading(false);
        setError("ERROR. Favor de comunicarse con sistemas.");
      }
    );
  };

  const filterChangeHandler = (filter) => {
    setFiltered(filter);
};

  const filteredListProduct = props.items.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  return (
    <div>

      <Filter onChangeFilter={filterChangeHandler}></Filter>
      <div className="contenedor-tabla">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">First</th>
              <th scope="col">Last</th>
              <th scope="col">Handle</th>
            </tr>
          </thead>
          <tbody>
            {filteredListProduct.map((prodcut, index) => {
              return [
                <tr key={index}>
                  <th scope="col">{prodcut.contry}</th>
                  <th scope="col">{prodcut.JobTitle}</th>
                  <th scope="col">{prodcut.EmailAddress}</th>
                  <th scope="col">{prodcut.FirstNameLastName}</th>
                </tr>,
              ];
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Products;
